  return {
    ...tool,
    features: JSON.parse(tool.features),
    screenshots: JSON.parse(tool.screenshots),
    pros: JSON.parse(tool.pros),
    cons: JSON.parse(tool.cons),
    bestFor: JSON.parse(tool.bestFor),
    pricing: tool.pricing as Tool['pricing'],
  };
}

